package com.bucketlist.controller;

import com.bucketlist.model.Entry;
import com.bucketlist.service.EntryService;
import org.everit.json.schema.Schema;
import org.everit.json.schema.loader.SchemaLoader;
import org.json.JSONObject;
import org.json.JSONTokener;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.core.io.ClassPathResource;
import org.springframework.http.HttpStatus;
import org.springframework.web.bind.annotation.*;

import java.io.FileNotFoundException;
import java.io.IOException;
import java.util.List;
import java.util.Map;
import java.util.HashMap;

@RestController
@RequestMapping("/ester")
public class EntryController {

    @Autowired
    private EntryService entryService;
    private JSONObject jsonSubject;
    private JSONObject jsonSchema;

    @RequestMapping(method = RequestMethod.GET)
    public List<Entry> getAllEntries() {
        return entryService.getAllEntries();
    }

    @RequestMapping(method = RequestMethod.PUT)
    public Entry createEntry(@RequestParam(value = "title", required = true) String title,
                             @RequestParam(value = "content", required = true) String content) {
        return entryService.createEntry(title, content, false);
    }

    @RequestMapping(method = RequestMethod.POST)
    public Entry editEntry(@RequestBody(required = true) Entry entry) {
        JSONObject jsonSchema = null;
        try {
            jsonSchema = new JSONObject(
                    new JSONTokener(new ClassPathResource("entry_schema.json").getInputStream()));
            Schema schema = SchemaLoader.load(jsonSchema);
            schema.validate(new JSONObject(entry));
        } catch (FileNotFoundException e) {
            e.printStackTrace();
        } catch (IOException e) {
            e.printStackTrace();
        }

        return entryService.editEntry(entry);
    }

    @RequestMapping(value = "/{id}", method = RequestMethod.DELETE)
    @ResponseStatus(value = HttpStatus.OK)
    public void deleteEntry(@PathVariable Long id) {
        entryService.deleteEntry(id);
    }

    @RequestMapping(value = "/data/getImageByIsbn", method = RequestMethod.GET)
    public Map<String, String> getBook(@RequestParam(value = "isbnCode", required = true) String isbnCode) {
        Map<String, String> sampleBook = new HashMap<>();
        sampleBook.put("isbnCode", isbnCode);
        sampleBook.put("imageData", "data:image/jpeg;base64,");
        return sampleBook;
    }
}
